    i.icon-star {
      color: @subtle !important;
    }

    /* RIGHT FRONTPAGE SIDEBAR */

    /* reddit premium icon */
    i.icon-premium_fill {
      color: @accent !important;
    }

    /* reddit premium try now button */
    button._10BQ7pjWbeYP63SAPNS8Ts.q_unSaY23rpdd3lDvGZ- {
      background: @accent !important;
      color: @surface !important;
    }

    /* recent posts thumbnail borders */
    div._3fWuhJ6bVet7XJT5A0mZM2 > div > div > div {
      border-color: @subtle !important;
    }

    /* POSTS */

    /* post background for card style */
    div[data-adclicklocation="background"] {
      background: @surface !important;
    }

    /* post options (save, comment, etc.) */
    div._3-miAEojrCvx_4FQ8x3P-s,
    i.icon-comment,
    i.icon-share,
    i.icon-save,
    i.icon-expand,
    i.icon-collapse,
    i.icon-text_post,
    i.icon-image_post,
    i.icon-media_gallery,
    i.icon-video_post,
    i.icon-gif_post,
    i.icon-embed,
    i.icon-external_link,
    i.icon-poll_post {
      color: @subtle !important;
    }

    /* post title color */
    div[data-adclicklocation="title"] > div > a > div > h3,
    div[data-adclicklocation="title"]
      > div:first-child
      > div:first-child
      > h1:first-child {
      color: @text !important;
    }

    /*
     *  post op color and
     *  post date color
     */
    div[data-adclicklocation="top_bar"] > span,
    a[data-testid="post_author_link"] {
      color: @subtle !important;
    }

    /* nsfw flair */
    span._1wzhGvvafQFOWAyA157okr {
      border-color: @anis !important;
      color: @anis !important;
    }

    /* pinned post icon */
    i.icon-pin_fill {
      color: @uvs !important;
    }

    /* give award icon */
    i.icon-award {
      color: @subtle !important;
    }

    /* locked post icon */
    i.icon-archived_fill,
    i.icon-lock_fill {
      color: @els !important;
    }

    /* post button divider */
    div[data-click-id="body"] > div:nth-child(3) > div:nth-child(3) {
      border-color: @overlay !important;
    }

    /* UPVOTES/DOWNVOTES */

    /* upvote/downvote background */
    div.Post,
    div[data-click-id="media"] {
      background: @base !important;
      fill: @base !important;
    }

    /* inactive vote text color */
    div[id^="vote-arrows"] > div {
      color: @subtle !important;
    }

    /* active upvote color */
    i.icon-upvote_fill {
      color: @anis !important;
    }

    button[aria-label="upvote"][aria-pressed="false"] > span:hover > i {
      color: @anis !important;
    }

    /* active upvote text color */
    button[aria-label="upvote"][aria-pressed="true"] ~ div {
      color: @anis !important;
    }

    /* hover background for both upvotes and downvotes */
    button[aria-label="upvote"][aria-label="downvote"] > span:hover {
      background: @surface !important;
    }

    /* active downvote color */
    .icon-downvote_fill {
      color: @nuur !important;
    }

    /* active downvote text color */
    button[aria-label="downvote"][aria-pressed="true"] ~ div {
      color: @nuur !important;
    }

    button[aria-label="downvote"][aria-pressed="false"] > span:hover > i {
      color: @nuur !important;
    }

    /* REPORT MODAL */

    a.o1ov2mzLxTFAFP-O4Uv8I {
      color: @accent !important;
    }

    button._1lDGFVGU9k2mi-4kjU95Rp {
      color: @surface !important;
    }

    /* SUBREDDIT PAGES */
    a[role="button"][target="_blank"] {
      background: @text !important;
      color: @surface !important;
    }

    /* radio buttons (flair selector, etc.) */
    div[role="radio"]:hover,
    div[role="radio"]:focus {
      background: @accent !important;
    }

    div[role="radio"] > svg,
    div[role="radio"] > svg:active {
      fill: @text !important;
    }

    /* checkboxes */
    button[role="checkbox"] {
      fill: @accent !important;
    }

    /* SUBREDDIT RIGHT SIDEBAR (some styles carry over from the homepage right sidebar section) */

    /* header background */
    ._ZhON3a3vplThB8NFwuJn {
      background: @base !important;
      color: @text !important;
    }

    /* subreddit image background */
    img[alt="Subreddit Icon"][role="presentation"] {
      background: @accent !important;
    }

    /* border between uela */
    ._1KrMye71CT332tKKKUWAj6:not(:last-of-type) {
      border-bottom-color: @surface;
    }

    /* follower color */
    .bg-neutral-background {
      color: @text !important;
    }

    /* default subreddit icon */
    i.icon-community_fill {
      color: @accent !important;
    }

    /* online users */
    div._21RLQh5PvUhC6vOKoFeHUP::before {
      color: @uvs !important;
    }

    /* POST VIEWER */

    /* spoiler tags */
    ._2v4IIjPhKL0PDaWaWtjJ1E {
      background: @surface !important;
    }

    /* mod comment tag */
    span[id^="CommentTopMeta--Mod"] {
      color: @uvs !important;
    }

    /* op comment tag */
    span[id^="CommentTopMeta--OP"] {
      color: @nuur !important;
    }

    /* post overlay if you have "open in new tab" disabled */
    div#overlayScrollContainer > div {
      background: @base !important;
    }

    /* fixes comment section background not working when "open in new tab is disabled" */
    div._2M2wOqmeoPVvcSsJ6Po9-V._3287nL7j7epK9JmDC3N1VR {
      background: @base !important;
    }

    /* shade applied to background when clicking on a post with "open in new tab" disabled */
    div._2DJXORCrmcNpPTSq0LqL6i,
    div._1DK52RbaamLOWw5UPaht_S {
      background: fade(@base, 80%) !important;
    }

    /* gold award gradient */
    div.TmlaIdEplCzZ0F1aRGYQh[role="presentation"] {
      background: linear-gradient(
        188deg,
        #f9e2af1f 1.7%,
        #f9e2af00 46%,
        rgba(0, 0, 0, 0)
      ) !important;
    }

    /* image gallery next/previous buttons */
    a[title="Next"] > i,
    a[title="Previous"] > i {
      background: @surface !important;
    }

    /* image gallery image count */
    div._61i6grM3um1yuw4GrN97P {
      background: fade(@base, 80%) !important;
      color: @text !important;
    }

    /* comment search bar */
    input#comment_search-bar {
      background: @surface !important;
    }

    /* tab bar below banner */
    ._1gVVmSnHZpkUgVShsn7-ua {
      background: @base !important;
    }

    /* MULTIREDDITS/CUSTOM FEEDS */

    /* delete custom feed button */
    .bX6SqXfzfxpv4GQbuIYVZ {
      color: @anis !important;
    }

    /* TOOLTIPS */
    div.HQ2VJViRjokXpRbJzPvvc {
      background: @base !important;
      color: @text !important;
    }

    div.HQ2VJViRjokXpRbJzPvvc::after {
      border-top-color: @base !important;
    }

    /* Recap Reddit Logo */
    .snoo-cls-1,
    .snoo-cls-2,
    .snoo-cls-3,
    .snoo-cls-6,
    .snoo-cls-8,
    .snoo-cls-9 {
      fill: @surface;
    }

    .snoo-cls-4,
    .snoo-cls-5,
    .snoo-cls-7,
    .snoo-cls-10,
    .snoo-cls-11 {
      fill: @accent;
    }
  }
}

/* deno-fmt-ignore */
@sarnai-theme: {
  @ovol:   { @sarnai: #b93d4d; @anis: #cc2929; @chatsalgan: #da730c; @els: #df9b23; @uvs: #4c890f; @nuur: #0a728c; @mus: #12adad; @yargui: #a353c6; @text: #13201b; @subtle: #446f5e; @muted: #81b19e; @overlay: #90bbaa; @surface: #b6d2c7; @base: #dceae4; };
  @khavar: { @sarnai: #f0c3cb; @anis: #ff6b6b; @chatsalgan: #e5951a; @els: #cca24d; @uvs: #80b946; @nuur: #2b879e; @mus: #9deaea; @yargui: #d5b3e5; @text: #dfece7; @subtle: #90bbaa; @muted: #4e7e6b; @overlay: #2d493d; @surface: #21362d; @base: #172620; };
};

/* ==UserStyle==
@name Sarnai Npm
@namespace github.com/titembaatar/sarnai/userStyles/npm
@homepageURL https://github.com/titembaatar/sarnai
@version 0.1.0
@description Mongolian-inspired theme for Npm
@author Titembaatar
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["ovol:Ovol*", "khavar:Khavar"]
@var select darkFlavor "Dark Flavor" ["ovol:Ovol", "khavar:Khavar*"]
@var select accentColor "Accent" ["sarnai:Sarnai*", "anis:Anis", "chatsalgan:Chatsalgan", "els:Els", "uvs:Uvs", "nuur:Nuur", "mus:Mus", "yargui:Yargui"]
==/UserStyle== */

@-moz-document domain("www.npmjs.com") {
  :root {
    @media (prefers-color-scheme: light) {
      #sarnai-theme(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #sarnai-theme(@darkFlavor);
    }
  }

  #sarnai-theme(@flavor) {
    @sarnai: @sarnai-theme[@@flavor][@sarnai];
    @anis: @sarnai-theme[@@flavor][@anis];
    @chatsalgan: @sarnai-theme[@@flavor][@chatsalgan];
    @els: @sarnai-theme[@@flavor][@els];
    @uvs: @sarnai-theme[@@flavor][@uvs];
    @mus: @sarnai-theme[@@flavor][@mus];
    @nuur: @sarnai-theme[@@flavor][@nuur];
    @yargui: @sarnai-theme[@@flavor][@yargui];
    @text: @sarnai-theme[@@flavor][@text];
    @subtle: @sarnai-theme[@@flavor][@subtle];
    @muted: @sarnai-theme[@@flavor][@muted];
    @overlay: @sarnai-theme[@@flavor][@overlay];
    @surface: @sarnai-theme[@@flavor][@surface];
    @base: @sarnai-theme[@@flavor][@base];
    @accent: @sarnai-theme[@@flavor][@@accentColor];

    @anis-filter: @sarnai-theme-filters[@@flavor][@anis];

    color-scheme: if(@flavor = ovol, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      background-color: transparent;

      &:focus {
        box-shadow: 0 0 0 2px @accent;
      }

      &::placeholder {
        color: @subtle !important;
      }
    }

    accent-color: @accent;

    @npmGradient: linear-gradient(139deg, @chatsalgan, @anis, @anis, @sarnai);

    --color-fg-default: @text;
    --color-fg-muted: @text;
    --color-fg-subtle: @subtle;
    --color-fg-on-emphasis: @surface;
    --color-fg-brand: @accent;
    --color-fg-accent: @nuur;
    --color-fg-success: @uvs;
    --color-fg-attention: @els;
    --color-fg-danger: @anis;
    --color-border-strong: @surface;

    --header-color: @text;
    /* Used on deprecation confirmation page */
    --wombat-red: @anis;
    --wombat-red-hover: @anis;

    --search-fg-muted: @text;
    --search-border: @surface;
    --search-bg-subtle: @surface;

    &,
    main {
      background-color: @surface !important;
      color: @text;
    }

    /* Primary background */
    .bg-white,
    ._4ea0e50d {
      background-color: @surface;
    }

    /* Background layer background */
    .bg-black-05,
    ._7eb68a55,
    .e9998f88 {
      background-color: @base;
    }

    /* Primary text */
    .black,
    .black-90,
    .black-80,
    a {
      color: @text;
    }

    /* Secondary text */
    .black-70,
    .black-60,
    .dim:focus,
    .dim:hover {
      color: @subtle;
    }

    /* Inverse text */
    .white {
      color: @surface;
    }

    /* Border colors */
    .b--black-10,
    .b--black-20 {
      border-color: @surface;
    }

    /* --- ARBITRARY --- */

    /* Top loading bar */
    div.fixed.top-0.left-0.z-999 {
      background-color: @accent;
    }

    /* Magnifying glass icon (search box, discover packages) */
    svg g[stroke="#777777"] {
      stroke: @subtle;
    }

    /* "Beta" pill */
    .a17280e0 {
      color: @uvs;
      border-color: @uvs;
    }
    /* Green checkmark */
    [fill="#107010"] {
      fill: @uvs;
    }

    /* --- HEADER --- */
    [data-test-id="notification-banner"] {
      background-color: if(
        @flavor = latte,
        desaturate(lighten(@uvs, 30%), 5%),
        desaturate(darken(@uvs, 5%), 5%)
      );
      border-color: @uvs;

      #notification,
      button[aria-label="Close notification"] {
        color: if(@flavor = latte, @text, @surface);
      }
    }
    header {
      border-bottom-color: @surface;

      /* Top bar gradient */
      .e7070742 {
        border-image: @npmGradient 3;
      }

      /* Heart icon */
      ._0edb515f {
        color: @text;
      }

      /* Logo */
      a[href="/"] > svg > path {
        fill: @text;
      }

      /* Search box */
      form#search {
        /* Search button */
        button[type="submit"] {
          background-color: @text;
        }

        /* Search input wrapper */
        > div {
          border-color: @surface;

          &:focus-within {
            border-color: @text;
          }

          /* Search input */
          div:has(input[type="search"]) {
            background-color: @base;

            input[type="search"] {
              color: @text;

              &:focus {
                box-shadow: none;
              }

              &::-webkit-search-cancel-button {
                display: none;
              }
            }
          }

          /* Search results  (floating box) */
          div.list {
            background-color: @surface;

            ul[aria-label="Search results"] li {
              border-bottom-color: @surface;

              &:hover {
                background-color: @overlay;
              }
            }
          }
        }
      }

      /* Profile picture navigation */
      nav:has(> button[aria-label="Profile menu"]) {
        > button {
          /* Dropdown arrow */
          img[alt="avatar"] {
            border-color: @text;

            + div > svg {
              fill: @text;
            }
          }
        }

        /* Popup */
        > div > div {
          border-color: @surface;

          /* Popup triangle */
          &::after {
            border-bottom-color: @surface;
          }
          &::before {
            border-bottom-color: @surface;
          }

          /* Username */
          h2 {
            border-bottom-color: @overlay;

            span {
              color: @text;
            }
          }

          /* Navigation options */
          ul li a {
            &:hover,
            &:focus {
              background-color: @base;
            }

            /* Red "Add Organization" button */
            &[href="/org/create"] {
              border-color: @surface;
              color: @accent;
              svg {
                fill: @accent;
              }
            }
          }
        }
      }
    }

    /* --- HOME PAGE --- */

    ._8c1ee087 {
      background-image:
        linear-gradient(
          270deg,
          fade(@anis, 16%) 0%,
          fade(@anis, 56%) 18.45%,
          fade(@anis, 80%) 49.67%,
          fade(@anis, 56%) 82.52%,
          fade(@anis, 19%) 99.7%,
          fade(@anis, 0%) 99.71%,
          fade(@anis, 0%) 99.72%,
          fade(@anis, 16%) 99.73%
        ),
        url("https://static-production.npmjs.com/abf53a31b2da4657a1a004ee9358551c.png");

      a[href="/signup"] {
        background-color: @els;
      }
      a[href="/products/pro"] {
        background-color: @anis;
        border-color: @text;
      }
    }
    article > section:nth-of-type(2) {
      color: @text;
    }

    /* Popular libraries section */
    #popular-libraries-header + hr {
      border-color: @accent;
    }
    ul[aria-labelledby="popular-libraries-header"] {
      li a {
        border-bottom-color: @surface;
      }
    }

    /* Discover packages section */
    #discover-packages-header + hr {
      border-color: mix(@anis, @els);
    }
    ul[aria-labelledby="discover-packages-header"] li {
      a {
        color: @text;
        border-color: @surface;
      }
      &:nth-of-type(6n+1):hover a {
        color: @yargui;
        border-bottom-color: @yargui;
      }
      &:nth-of-type(6n+2):hover a {
        color: @uvs;
        border-bottom-color: @uvs;
      }
      &:nth-of-type(6n+3):hover a {
        color: @mus;
        border-bottom-color: @mus;
      }
      &:nth-of-type(6n+4):hover a {
        color: @anis;
        border-bottom-color: @anis;
      }
      &:nth-of-type(6n+5):hover a {
        color: @els;
        border-bottom-color: @els;
      }
      &:nth-of-type(6n+6):hover a {
        color: @sarnai;
        border-bottom-color: @sarnai;
      }
    }

    /* By the numbers section */
    ._8f26e3fd {
      border-color: @els;
    }

    /* Recently updated packages section */
    #recently-updated-packages-header + hr {
      border-color: @accent;
    }

    /* --- PACKAGE PAGES --- */

    /* Deprecation warning */
    .bg-washed-red {
      background-color: fade(@anis, 30%);

      code {
        background-color: @base;
        color: @text;
      }
    }
    .red {
      color: @anis !important;
    }

    /* "public" text */
    ._813b53b2 {
      color: @uvs;
    }
    /* TypeScript logo */
    img[alt="TypeScript icon, indicating that this package has built-in type declarations"] {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 256 256" xml:space="preserve"><path fill="@{nuur}" d="M0 0h256v256H0z"/><path d="M150.5 200.5v27.6c4.5 2.3 9.8 4 15.9 5.2s12.6 1.7 19.4 1.7c6.6 0 12.9-.6 18.9-1.9s11.2-3.4 15.7-6.3 8-6.7 10.7-11.4q3.9-7.05 3.9-17.4c0-5-.7-9.4-2.2-13.2s-3.7-7.1-6.5-10.1c-2.8-2.9-6.2-5.6-10.1-7.9s-8.4-4.5-13.3-6.6c-3.6-1.5-6.9-2.9-9.8-4.4-2.9-1.4-5.3-2.8-7.3-4.3s-3.6-3-4.7-4.7-1.6-3.5-1.6-5.6q0-2.85 1.5-5.1c1.5-2.25 2.4-2.8 4.1-3.9 1.8-1.1 4-1.9 6.6-2.5s5.5-.9 8.6-.9c2.3 0 4.7.2 7.3.5s5.1.9 7.7 1.6 5.1 1.6 7.6 2.7c2.4 1.1 4.7 2.4 6.8 3.8v-25.8q-6.3-2.4-13.8-3.6c-7.5-1.2-10.7-1.2-17.1-1.2-6.6 0-12.8.7-18.7 2.1s-11 3.6-15.5 6.6-8 6.8-10.6 11.4-3.9 10.2-3.9 16.6c0 8.2 2.4 15.2 7.1 21.1 4.8 5.8 12 10.7 21.6 14.8 3.8 1.6 7.3 3.1 10.6 4.6s6.1 3 8.5 4.7 4.3 3.4 5.7 5.3 2.1 4.1 2.1 6.5c0 1.8-.4 3.4-1.3 5-.9 1.5-2.2 2.8-3.9 4s-3.9 2-6.6 2.6-5.7.9-9.2.9c-6 0-11.9-1.1-17.8-3.2-5.9-2-11.4-5.1-16.4-9.3m-46-68.8H140V109H41v22.7h35.3V233h28.1V131.7z" fill-rule="evenodd" clip-rule="evenodd" fill="@{base}"/></svg>'
      );
      content: url("data:image/svg+xml,@{svg}");
    }
    /* DefinitelyTyped logo */
    img[alt="DefinitelyTyped icon, indicating that this package has TypeScript declarations provided by the separate @types/bootstrap package"] {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 256 256" xml:space="preserve"><path fill="@{nuur}" d="M0 0h256v256H0z"/><path fill="@{base}" d="M6 6h244v244H6z"/><path fill="@{nuur}" d="M41.8 111.5c8.6-1.6 19.9-2.5 31.8-2.5 19.7 0 32.6 4.2 42.6 13 10.8 9.4 17.6 24.5 17.6 46 0 23.4-7.3 39.5-17.3 49.5-11 10.7-27.6 15.8-48 15.8-12.2 0-20.8-.9-26.7-1.8zm23.6 99.7c2 .5 5.2.5 8.2.5 21.3.2 35.2-13.6 35.2-42.7.2-25.4-12.5-38.8-32.7-38.8-5.2 0-8.6.5-10.6 1.1v79.9zm104-76.5h-32.2v-22.8h92.3v22.8h-32.8V232h-27.3z"/></svg>'
      );
      content: url("data:image/svg+xml,@{svg}");
    }
    /* Navigation tabs */
    #package-tab(@color) {
      border-color: @color;

      &:has(> a[aria-selected="false"]):hover,
      &:has(> a[aria-selected="true"]) {
        background-color: fade(@color, 20%);
      }

      a {
        color: @color;
      }
    }
    ul[aria-owns*="package-tab-"] {
      li {
        &:has(#package-tab-readme),
        &:has(#package-tab-members) {
          #package-tab(@els);
        }
        &:has(#package-tab-code),
        &:has(#package-tab-admin),
        &:has(#package-tab-packages) {
          #package-tab(@anis);
        }
        &:has(#package-tab-dependents),
        &:has(#package-tab-billing) {
          #package-tab(@yargui);
        }
        &:has(#package-tab-dependencies) {
          #package-tab(@sarnai);
        }
        &:has(#package-tab-versions) {
          #package-tab(@nuur);
        }
        &:has(#package-tab-orgs),
        &:has(#package-tab-teams) {
          #package-tab(@uvs);
        }
      }
    }

    /* Sidebar icons */
    svg:has(+ p > [aria-label="Copy install command line"]),
    button[aria-label="Copy install command line"] svg path,
    [aria-labelledby="repository repository-link"] svg g,
    [aria-labelledby="homePage homePage-link"] svg path,
    [aria-label="Showing weekly downloads"] svg {
      fill: @text;
    }
    /* Copy install command box */
