    /* Icons */

    [fill="@{anis}"],
    [fill="#F00"],
    [fill="#FF0000"],
    [fill="#f03"],
    [fill="#FF0033"] {
      fill: @accent !important;
    }

    [fill="white"] {
      fill: if(@flavor = latte, @surface, @text) !important;
    }

    /* verification badge */
    [src*="https://www.gstatic.com/images/icons/material/system/1x/check_circle_grey600_36dp.png"] {
      filter: @text-filter;
    }

    yt-icon.ytd-logo [fill="white"] {
      fill: @base !important;
    }

    .yt-spec-icon-shape {
      [fill="#FAFAFA"],
      [fill="#000"],
      [fill="#fff"],
      [fill="#FFF"],
