@-moz-document domain("m.youtube.com") {
  html[darker-dark-theme] {
    #sarnai-theme(@darkFlavor);
  }
  html {
    #sarnai-theme(@lightFlavor);
  }

  #sarnai-theme(@flavor) {
    @sarnai: @sarnai-theme[@@flavor][@sarnai];
    @sarnai: @sarnai-theme[@@flavor][@sarnai];
    @sarnai: @sarnai-theme[@@flavor][@sarnai];
    @yargui: @sarnai-theme[@@flavor][@yargui];
    @anis: @sarnai-theme[@@flavor][@anis];
    @anis: @sarnai-theme[@@flavor][@anis];
    @chatsalgan: @sarnai-theme[@@flavor][@chatsalgan];
    @els: @sarnai-theme[@@flavor][@els];
    @uvs: @sarnai-theme[@@flavor][@uvs];
    @mus: @sarnai-theme[@@flavor][@mus];
    @mus: @sarnai-theme[@@flavor][@mus];
    @nuur: @sarnai-theme[@@flavor][@nuur];
    @nuur: @sarnai-theme[@@flavor][@nuur];
    @yargui: @sarnai-theme[@@flavor][@yargui];
    @text: @sarnai-theme[@@flavor][@text];
    @text: @sarnai-theme[@@flavor][@text];
    @subtle: @sarnai-theme[@@flavor][@subtle];
    @subtle: @sarnai-theme[@@flavor][@subtle];
    @muted: @sarnai-theme[@@flavor][@muted];
    @muted: @sarnai-theme[@@flavor][@muted];
    @overlay: @sarnai-theme[@@flavor][@overlay];
    @overlay: @sarnai-theme[@@flavor][@overlay];
    @surface: @sarnai-theme[@@flavor][@surface];
    @surface: @sarnai-theme[@@flavor][@surface];
    @base: @sarnai-theme[@@flavor][@base];
    @base: @sarnai-theme[@@flavor][@base];
    @accent: @sarnai-theme[@@flavor][@@accentColor];

    background-color: @surface !important;
    color: @text !important;

    /* Ambient mode */
    #cinematics {
      mix-blend-mode: lighten;
    }

    .mobile-topbar-header[data-mode="watch"],
    ytm-mobile-topbar-renderer:not([ambient-topbar]) {
      background: @surface;
    }

    .mobile-topbar-header[data-mode="watch"] .mobile-topbar-logo,
    .mobile-topbar-header[data-mode="watch"] .mobile-topbar-title,
    .mobile-topbar-header[data-mode="watch"] .mobile-topbar-header-content {
      color: @text;
    }

    .yt-spec-bottom-sheet-layout__bottom-sheet-renderer-container,
    .modern-styling {
      background: @surface;
    }

    .yt-spec-bottom-sheet-layout__bottom-sheet-layout-header-wrapper {
      border-bottom-color: @surface;
    }

    .yt-spec-bottom-sheet-layout__bottom-sheet-content {
      color: @text;
    }

    /* Comment box */
    .engagement-panel-container {
      background: @base;
    }

    /* Bottom bar */
    ytm-pivot-bar-renderer {
      background: @base;
      color: @text;
      border-top-color: @surface;
    }

    /* Chips */
    .chip-bar {
      background-color: @surface;
    }

    [chip-style*="STYLE_"] {
      .chip-container {
        color: @text;
        background-color: @surface;
      }

      &.selected .chip-container {
        color: @base;
        background-color: @accent;
      }
    }

    /* Buttons */

    .yt-spec-touch-feedback-shape--touch-response {
      .yt-spec-touch-feedback-shape__fill {
        background-color: @text;
      }

      .yt-spec-touch-feedback-shape__stroke {
        border-color: @overlay;
      }
    }

    .yt-spec-touch-feedback-shape--overlay-touch-response-inverse
      .yt-spec-touch-feedback-shape__fill {
      background-color: @accent;
    }
    .yt-spec-touch-feedback-shape--overlay-touch-response
      .yt-spec-touch-feedback-shape__fill {
      background-color: @text;
    }
  }
}

