/* ==UserStyle==
@name Sarnai Haveibeenpwned
@namespace github.com/titembaatar/sarnai/userStyles/haveibeenpwned
@homepageURL https://github.com/titembaatar/sarnai
@version 0.1.0
@description Mongolian-inspired theme for Haveibeenpwned
@author Titembaatar
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["ovol:Ovol*", "khavar:Khavar"]
@var select darkFlavor "Dark Flavor" ["ovol:Ovol", "khavar:Khavar*"]
@var select accentColor "Accent" ["sarnai:Sarnai*", "anis:Anis", "chatsalgan:Chatsalgan", "els:Els", "uvs:Uvs", "nuur:Nuur", "mus:Mus", "yargui:Yargui"]
==/UserStyle== */

@-moz-document domain("haveibeenpwned.com") {
  :root {
    @media (prefers-color-scheme: light) {
      #sarnai-theme(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #sarnai-theme(@darkFlavor);
    }
  }

  #sarnai-theme(@flavor) {
    @sarnai: @sarnai-theme[@@flavor][@sarnai];
    @anis: @sarnai-theme[@@flavor][@anis];
    @chatsalgan: @sarnai-theme[@@flavor][@chatsalgan];
    @els: @sarnai-theme[@@flavor][@els];
    @uvs: @sarnai-theme[@@flavor][@uvs];
    @mus: @sarnai-theme[@@flavor][@mus];
    @nuur: @sarnai-theme[@@flavor][@nuur];
    @yargui: @sarnai-theme[@@flavor][@yargui];
    @text: @sarnai-theme[@@flavor][@text];
    @subtle: @sarnai-theme[@@flavor][@subtle];
    @muted: @sarnai-theme[@@flavor][@muted];
    @overlay: @sarnai-theme[@@flavor][@overlay];
    @surface: @sarnai-theme[@@flavor][@surface];
    @base: @sarnai-theme[@@flavor][@base];
    @accent: @sarnai-theme[@@flavor][@@accentColor];

    @text-filter: @sarnai-theme-filters[@@flavor][@text];

    color-scheme: if(@flavor = olvo, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtle !important;
      }
    }

    body {
      background-color: @surface;
      color: @text;
    }

    hr {
      border-color: @surface;
    }

    a {
      &,
      &:hover,
      &:focus {
        color: @text;
        border-bottom-color: @overlay;
      }

      &:hover {
        background-color: fade(@accent, 30%);
      }
    }

    img.pwnLogo.large.modalLogo {
      filter: @text-filter;
    }

    .bodyGradient {
      color: @text;
    }

    .modal-header {
      background: @base;

      .close {
        color: @text;
      }
    }

    .fa-loader,
    .loaderText {
      color: @text;
    }

    #postNotificationCallsToAction {
      .btn {
        background-color: @base;
        color: @text;
      }

      a.socialLink {
        &,
        &:link,
        &:visited {
          color: @text;
        }
      }
    }

    .modal-content {
      background-color: @surface;
      border-color: @surface;
    }

    .main {
      background-color: @base;
      color: @text;
    }

    .main-logo .logo {
      border-color: @accent;
      background-color: fade(@accent, 30%);
    }

    .pwnedCompanyList {
      a,
      a:hover {
        color: @text;
        border-bottom-color: @overlay;
      }

      td {
        color: @subtle;
      }
    }

    footer {
      a,
      p {
        color: @subtle;
      }
    }

    .unverified {
      &,
      a {
        color: darken(@anis, 50%) !important;
        border-bottom-color: darken(@anis, 50%) !important;
      }
    }

    .navbar-inverse {
      border-color: @surface;
      background-color: @base;

      .navbar-toggle {
        border-color: @surface;

        &:hover,
        &:focus {
          background-color: @surface;
        }

        .icon-bar {
          background-color: @text;
        }
      }

      .navbar-brand {
        color: @text;
        border-color: @text !important;
      }

      li.active > a {
        &,
        &:hover,
        &:focus {
          color: @text;
          background-color: @base;
        }
      }

      li > a {
        color: @subtle;

        &:hover,
        &:focus {
          color: @text;
        }
      }
    }

    img.pwnLogo {
      &[src$="/List.png"],
      &[src$="/Email.png"] {
        filter: @text-filter;
      }
    }

    .form-control {
      background-color: @base;
      color: @text;
      border-color: @surface !important;

      &:focus {
        box-shadow: 0 0 8px fade(@accent, 30%);
      }
    }

    .btn-primary {
      color: @text;
      background-color: @base;
      border-color: @surface !important;

      &:hover {
        background-color: fade(@accent, 30%);
      }
    }

    .btn-success {
      background-color: @uvs;
      color: @surface;
      border-color: @surface;
    }

    .progress-bar {
      background-color: @accent;
    }

    .form-group .termsOfUse {
      background-color: @surface;
    }

    .termsOfUse {
      background-color: @base;

      &,
      a {
        color: @subtle;
        border-bottom-color: @overlay;
      }
    }

    .secondaryHeader {
      background-color: @base;
    }

    .sensitive {
      color: @els;
    }

    .sensitive-fade {
      color: @subtle;
    }

    .panel-primary {
      border-color: @surface;

      > .panel-heading,
      > .panel-footer {
        color: @text;
        border-color: @surface;
        background-color: @base;
      }
    }

    #noPwnage {
      background: @uvs;

      a.socialLink {
        color: @surface !important;
      }
    }

    #invalidAccount {
      background: @els;
    }

    #pwnedWebsitesContainer {
      hr {
        border-color: @surface;
      }

      > .pwnedRow {
        background: @anis;

        .pwnedWebsite {
          color: @surface;

          a {
            color: @surface;
            border-bottom-color: @surface;

            &:hover {
              background-color: transparent !important;
            }
          }
        }
      }

      a.socialLink {
        color: @surface !important;
      }
    }

    .pwnResultBanner {
      color: @surface;

      a {
        color: @surface;
        border-bottom-color: @surface !important;

        &:hover {
          background-color: transparent !important;
        }
      }
    }

    #Cloudflare img {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1239.3 310.1"><path d="M1158.5 187.7c-3.4 0-6.1-2.7-6.1-6.1 0-3.3 2.7-6.1 6.1-6.1 3.3 0 6.1 2.7 6.1 6.1s-2.8 6.1-6.1 6.1m0-11c-2.7 0-4.9 2.2-4.9 4.9s2.2 4.9 4.9 4.9 4.9-2.2 4.9-4.9-2.2-4.9-4.9-4.9m3.1 8.1h-1.4l-1.2-2.3h-1.6v2.3h-1.3v-6.7h3.2c1.4 0 2.3.9 2.3 2.2 0 1-.6 1.7-1.4 2zm-2.4-3.5c.5 0 1-.3 1-1 0-.8-.4-1-1-1h-2v2zM523 174.9h15.6v42.5h27.1V231H523zm58.8 28.2v-.1c0-16.1 13-29.2 30.3-29.2s30.1 12.9 30.1 29v.2c0 16.1-13 29.2-30.3 29.2-17.2-.1-30.1-13-30.1-29.1m44.6 0v-.1c0-8.1-5.8-15.1-14.4-15.1-8.5 0-14.2 6.9-14.2 15v.2c0 8.1 5.8 15.1 14.3 15.1 8.6-.1 14.3-7 14.3-15.1m34.9 3.3v-31.5h15.8v31.2c0 8.1 4.1 11.9 10.3 11.9s10.3-3.7 10.3-11.5v-31.6h15.8V206c0 18.1-10.3 26-26.3 26-15.9.1-25.9-8-25.9-25.6m76-31.5H759c20 0 31.7 11.5 31.7 27.7v.2c0 16.2-11.8 28.2-32 28.2h-21.3zm21.9 42.3c9.3 0 15.5-5.1 15.5-14.2v-.2c0-9-6.2-14.2-15.5-14.2h-6.3v28.5zm54-42.3h44.9v13.7h-29.4v9.5h26.6V211h-26.6v20h-15.5zm66.5 0h15.5v42.5h27.2V231h-42.7zm83.3-.4h15l23.9 56.5h-16.7l-4.1-10h-21.6l-4 10h-16.3zm13.7 34.4-6.2-15.9-6.3 15.9zm45.2-34h26.5c8.6 0 14.5 2.2 18.3 6.1 3.3 3.2 5 7.5 5 13.1v.2c0 8.6-4.6 14.3-11.5 17.2l13.4 19.6h-18l-11.3-17h-6.8v17H1022zm25.8 27c5.3 0 8.3-2.6 8.3-6.6v-.3c0-4.4-3.2-6.6-8.4-6.6h-10.2v13.5zm46.4-27h45.1v13.3h-29.7v8.4h26.9v12.3h-26.9v8.9h30.1V231h-45.5zM489 209.7c-2.2 4.9-6.8 8.4-12.8 8.4-8.5 0-14.3-7.1-14.3-15.1v-.2c0-8.1 5.7-15 14.2-15 6.4 0 11.3 3.9 13.3 9.3h16.4c-2.6-13.4-14.4-23.3-29.6-23.3-17.3 0-30.3 13.1-30.3 29.2v.2c0 16.1 12.8 29 30.1 29 14.8 0 26.4-9.6 29.4-22.4zm-97.9-40.1L348.2 145l-7.4-3.2-175.5 1.3v89l225.8.1z" fill="@{text}"/><path d="M313 224c2.1-7.2 1.3-13.8-2.2-18.7-3.2-4.5-8.6-7.1-15.1-7.4l-123.1-1.6c-.8 0-1.5-.4-1.9-1s-.5-1.4-.3-2.2c.4-1.2 1.6-2.1 2.9-2.2l124.2-1.6c14.7-.7 30.7-12.6 36.3-27.2l7.1-18.5c.3-.8.4-1.6.2-2.4-8-36.2-40.3-63.2-78.9-63.2-35.6 0-65.8 23-76.6 54.9-7-5.2-15.9-8-25.5-7.1-17.1 1.7-30.8 15.4-32.5 32.5-.4 4.4-.1 8.7.9 12.7-27.9.8-50.2 23.6-50.2 51.7 0 2.5.2 5 .5 7.5.2 1.2 1.2 2.1 2.4 2.1h227.2c1.3 0 2.5-.9 2.9-2.2zm39.2-79.1c-1.1 0-2.3 0-3.4.1-.8 0-1.5.6-1.8 1.4l-4.8 16.7c-2.1 7.2-1.3 13.8 2.2 18.7 3.2 4.5 8.6 7.1 15.1 7.4l26.2 1.6c.8 0 1.5.4 1.9 1s.5 1.5.3 2.2c-.4 1.2-1.6 2.1-2.9 2.2l-27.3 1.6c-14.8.7-30.7 12.6-36.3 27.2l-2 5.1c-.4 1 .3 2 1.4 2h93.8c1.1 0 2.1-.7 2.4-1.8 1.6-5.8 2.5-11.9 2.5-18.2 0-37.1-30.1-67.2-67.3-67.2" fill="@{chatsalgan}"/></svg>'
      );
      content: url("data:image/svg+xml,@{svg}");
    }

    pre,
    code {
      background-color: @base;
      color: @text;
      border-color: @surface;
    }

    thead tr {
      border-color: @surface;
    }

    .bitcoinAddress {
      background-color: @base;
      color: @text;
    }

    .bg-info {
      background-color: @base;
      color: @text;
    }

    .subscriptions {
      .rpm10 {
        border-color: @mus;

        h4,
        h5,
        .price {
          background-color: @mus;
          color: @surface;
        }
      }

      .rpm50 {
        border-color: @uvs;

        h4,
        h5,
        .price {
          background-color: @uvs;
          color: @surface;
        }
      }

      .rpm100 {
        border-color: @els;

        h4,
        h5,
        .price {
          background-color: @els;
          color: @surface;
        }
      }

      .rpm500 {
        border-color: @chatsalgan;

        h4,
        h5,
        .price {
          background-color: @chatsalgan;
          color: @surface;
        }
      }

      .description {
        color: @text;
      }
    }

    .finePrint {
      color: @text;
    }

    .table-bordered {
      &,
      th,
      td {
        border-color: @surface;
      }
    }

    .dropdown-menu {
      background-color: @surface;

      > li > a {
        &:hover,
        &:focus {
          background-color: @base;
        }
      }
    }

    @media (min-width: 768px) {
      ul.nav li.dropdown:hover {
        background-color: @surface;
      }
    }

    @media (max-width: 480px) {
      #searchContainer {
        background-color: @base;
      }
    }
  }
}


/* deno-fmt-ignore */
