/* ==UserStyle==
@name Sarnai Gmail
@namespace github.com/titembaatar/sarnai/userStyles/gmail
@homepageURL https://github.com/titembaatar/sarnai
@version 0.1.0
@description Mongolian-inspired theme for Gmail
@author Titembaatar
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["ovol:Ovol*", "khavar:Khavar"]
@var select darkFlavor "Dark Flavor" ["ovol:Ovol", "khavar:Khavar*"]
@var select accentColor "Accent" ["sarnai:Sarnai*", "anis:Anis", "chatsalgan:Chatsalgan", "els:Els", "uvs:Uvs", "nuur:Nuur", "mus:Mus", "yargui:Yargui"]
==/UserStyle== */

@-moz-document domain("mail.google.com") {
  :root {
    @media (prefers-color-scheme: light) {
      #sarnai-theme(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #sarnai-theme(@darkFlavor);
    }
  }

  #sarnai-theme(@flavor) {
    @sarnai: @sarnai-theme[@@flavor][@sarnai];
    @anis: @sarnai-theme[@@flavor][@anis];
    @chatsalgan: @sarnai-theme[@@flavor][@chatsalgan];
    @els: @sarnai-theme[@@flavor][@els];
    @uvs: @sarnai-theme[@@flavor][@uvs];
    @nuur: @sarnai-theme[@@flavor][@nuur];
    @mus: @sarnai-theme[@@flavor][@mus];
    @yargui: @sarnai-theme[@@flavor][@yargui];
    @text: @sarnai-theme[@@flavor][@text];
    @subtle: @sarnai-theme[@@flavor][@subtle];
    @muted: @sarnai-theme[@@flavor][@muted];
    @overlay: @sarnai-theme[@@flavor][@overlay];
    @surface: @sarnai-theme[@@flavor][@surface];
    @base: @sarnai-theme[@@flavor][@base];
    @accent: @sarnai-theme[@@flavor][@@accentColor];

    @base-filter: @sarnai-theme-filters[@@flavor][@base];
    @text-filter: @sarnai-theme-filters[@@flavor][@text];
    @els-filter: @sarnai-theme-filters[@@flavor][@els];

    color-scheme: if(@flavor = ovol, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtle !important;
      }

      caret-color: @text;
    }

    #input-styling {
      input,
      select,
      button {
        background-color: @base;
        color: @text;
        border-color: @surface;
        border-style: solid;
        border-width: 1px;
        box-shadow: none;
      }
    }

    * {
      text-shadow: none !important;
    }

    /* Gmail logo */
    [src="https://ssl.gstatic.com/ui/v1/icons/mail/rfr/logo_gmail_lockup_default_1x_r5.png"],
    [src="https://ssl.gstatic.com/ui/v1/icons/mail/rfr/logo_gmail_lockup_dark_1x_r5.png"] {
      height: unset !important;
      width: unset !important;
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" viewBox="17.727 14.319 30 22.5" width="30" height="22.5"><path fill="@{nuur}" d="M19.773 36.819h4.773V25.227l-6.819-5.114v14.659a2.044 2.044 0 0 0 2.045 2.045"/><path fill="@{uvs}" d="M40.909 36.819h4.773a2.044 2.044 0 0 0 2.045-2.045v-14.66l-6.819 5.114"/><path fill="@{els}" d="M40.909 16.364v8.864l6.819-5.114v-2.727c0-2.529-2.888-3.971-4.909-2.455"/><path fill="@{anis}" d="M24.545 25.227v-8.863l8.181 6.136 8.181-6.136v8.864l-8.181 6.136m-15-13.977v2.727l6.819 5.114v-8.864l-1.909-1.431c-2.025-1.517-4.91-.075-4.91 2.455"/></svg>'
      );
      content: url("data:image/svg+xml,@{svg}");
    }

    /* Tooltips */
    .T-ays-iP,
    .gb_Ac {
      background-color: @base;
      color: @text;
    }

    /* Buttons */

    /* Solid primary */
    .T-I-atl {
      background-color: @accent;
      color: @base;
    }

    /* Transparent primary */
    .l0,
    div.b8 .a8k,
    .bAo > .ad {
      color: @accent;

      &::before {
        background-color: @accent;
      }
    }

    /* Transparent secondary */
    .bs0 > .acM,
    .bti > .btg,
    .a8Y > .T-I-ax7 {
      color: @text;

      /* Disabled */
      &.acN {
        color: @subtle;
      }

      &:hover::before {
        background-color: @subtle;
      }
    }

    /* Header and sidebars */
    .w-asV,
    .bkL,
    .bhZ.bym,
    .bhZ.bjB,
    .bhZ.bym.baA {
      background-color: @base;
    }
    /* Header icons */
    .gb_Kc svg,
    .gb_Pc.gb_Uc svg,
    .gb_Lc .gb_rd .gb_sd,
    .gb_Lc .gb_rd .gb_Kc,
    .gb_Lc .gb_rd .gb_ud,
    .gb_Pc.gb_Uc .gb_sd {
      color: @text;
    }
    /* Search mail input */
    .gb_Kc .gb_hd {
      background-color: @overlay;

      .gb_je,
      .gb_qe,
      .gsan_a,
      .gsas_a,
      svg {
        color: @text;
      }
    }
    /* Search mail input on focus */
    .gb_hd.gb_id {
      background-color: @surface;
    }
    /* Active search / filter modal */
    .gssb_c {
      /* Icons */
      button svg,
      .asor {
        filter: @text-filter;
      }

      /* Recent searches */
      .aRp,
      .gssb_e,
      .gssb_m {
        background-color: @surface;
        color: @text;
      }

      /* Hovered recent searches */
      .gssb_i {
        background-color: @surface;

        > .gssb_a {
          border-left-color: @accent;
        }

        /* X icon */
        .gsaq_b {
          filter: @text-filter;
        }
      }

      .aSP,
      .gssb_e {
        border-color: @overlay;
      }

      /* Separator */
      .gssb_l {
        background-color: @surface;
      }

      /* Search results */

      /* Icons */
      .gsoi_0,
      .asor_o,
      .gsoi_msra {
        filter: @text-filter;
      }

      /* People involved */
      .asor_e {
        color: @subtle;
      }
    }
    /* Pill filters */
    .H2 {
      background-color: @surface;
      border-color: @surface;
      color: @text;
      fill: @text;

      &:hover {
        background-color: @base;
      }

      &.bws {
        background-color: @accent;
        color: @base;
        fill: @base;

        .HV {
          filter: @base-filter;
        }

        &:hover .H1 {
          background-color: darken(@accent, 5%);
        }
      }
    }
    .aqc .H2 {
      border-color: @text;
      color: @text;

      &.bws {
        background-color: @overlay;
        color: @base;
      }
    }
    /* Advanced search/filters modal */
    .ZF-z6,
    .ZF-zT,
    .ZF-Av .lJ,
    .ZF-Av .lN {
      background-color: @surface;

      &,
      .boo > .ZG,
      .boo .aQl > .J-JN-M-I-Jm,
      .boo .aaa > .J-JN-M-I-Jm,
      .boo .ZE > .J-JN-M-I-Jm {
        color: @text;
      }

      .btl.acK,
      .btn::before {
        filter: @text-filter;
      }

      /* Text inputs */
      .nr {
        background-color: @surface;
        color: @text;

        &:focus {
          box-shadow: inset 0 -1px 0 0 @accent;
        }
      }

      /* Calendar input */
      .bs5::after {
        filter: @text-filter;
      }
      .J-JB-KA,
      .J-JB-KA-JS,
      .J-JB-KA-Jk,
      .J-JB-KA-LG {
        background-color: @surface;
        color: @text;
        border-color: @surface;

        /* Past days */
        .J-JB-KA-Ku-Kk {
          color: @subtle;
        }
        /* Future days */
        .J-JB-KA-a1R-JB.J-JB-KA-LH,
        .J-JB-KA-a1R-JB.J-JB-KA-Ku-Kk,
        .J-JB-KA-JB.J-JB-KA-a1R-JB {
          color: @text;
        }

        /* Hovered day */
        .J-JB-KA-JB:not(.J-JB-KA-K8, .J-JB-KA-KO):hover {
          color: @base;

          &::before {
            background-color: fade(@accent, 70%);
          }
        }

        /* Selected day */
        .J-JB-KA-KO {
          color: @base;

          &::before {
            background-color: @accent;
          }
        }

        /* Unselected today */
        .J-JB-KA-K8 {
          color: @base;

          &::before {
            background-color: @subtle;
          }
        }
      }

      /* Checkbox inputs */
      .bs1 + .bs3,
      .btj + .aD {
        color: @text;

        &::before {
          filter: @text-filter;
        }
      }

      /* Dropdown/select inputs */
      .J-JN-M-I {
        color: @text;
      }
    }

    /* Sidebar selected item */
    .TK .TO.nZ:active,
    .TK .TO.ol:active,
    .TK .TO.nZ,
    .TK .TO.ol,
    .byl .TK .nZ.aBP,
    .byl .TK .nZ.aS3,
    .byl .TK .nZ.aS4,
    .byl .TK .nZ.aS5,
    .byl .TK .nZ.aS6,
    .VDSZAf.H5o3mc {
      background-color: @surface;
    }
    .TO.NQ,
    .n6 .ah9:hover,
    .n6 .ah9.aiu:hover,
    .n6 .ah9:focus,
    .TK .TO:active,
    .n6 .ah9.aiu:active,
    .CL:hover,
    .H5o3mc:not(.VDSZAf):hover,
    .CL.NQ,
    .H5o3mc:not(.VDSZAf).NQ {
      background-color: @overlay;
    }
    .byl .TK .TO.nZ > .aHS-bnt .n0,
    .TK .nZ > .aHS-bnt .bsU,
    .byl .TK .nZ.aS3 .nU > .n0,
    .TK .nZ.aS3 .bsU,
    .byl .TK .nZ.aS4 .nU > .n0,
    .TK .nZ.aS4 .bsU,
    .byl .TK .nZ.aS5 .nU > .n0,
    .TK .nZ.aS5 .bsU,
    .byl .TK .nZ.aS6 .nU > .n0,
    .TK .nZ.aS6 .bsU,
    .TK .TO.nZ .nU > .n0,
    .TK .ol.NQ .nU > .n0,
    .TK .TO.ol .nU > .n0,
    .TK .nZ .bsU,
    .TK .ol .bsU,
    .VDSZAf.H5o3mc > .Q7,
    .TO .nU > .n0,
    .TO.NQ .nU > .n0,
    .TO.nZ .nU > .n0,
    .ah9 > .CJ,
    .n3 > .CL > .CK,
    .aAv,
    .bsU {
      color: @text;
    }
    /* Icons */
    .nZ > .TN.aHS-bnt .qj,
    .TO:not(.nZ) > .TN > .qj:not([style*="background-color"]),
    .n6 .n4 .G-asx,
    .aAw .aAu,
    .pM,
    .TO.nZ > .TN > .qj,
    .n3 .CL::before {
      filter: @text-filter;
    }

    /* Compose email button */
    .z0 > .L3 {
      background-color: @accent;
      color: @base;

      &:focus {
        background-color: @text;
      }
    }
    .WR .z0 > .L3::before,
    .WR.anZ .z0 > .L3::before {
      filter: @base-filter;
    }

    /* Top section of inbox */
    .bkK > .nH {
      background-color: @surface;

      /* Icons */
      .bzn,
      .aqJ .amJ,
      .adg .adk {
        filter: @text-filter;
      }
    }
    /* Inbox categories */
    .aKx > .aKz {
      color: @text;
    }
    /* Icons */
    .aAy > div {
      filter: @text-filter;
    }
    /* Currently selected category */
    .J-KU-KO::before {
      background-color: @text;
    }
    /* Inbox categories hover */
    .aRz.J-KU:hover {
      background-color: @overlay;
    }
    /* Email count / pagination */
    div[aria-label="Show more messages"] {
      .Dj {
