      .rZ {
        background-color: @surface;
      }
    }

    /* Confirm popup modal */
    div[aria-modal="true"] {
      --gm3-dialog-refactored-container-color: @surface;
      --gm3-dialog-refactored-supporting-text-color: @text;
      --gm3-dialog-refactored-headline-color: @text;

      /* Buttons */
      [data-mdc-dialog-action="ok"] {
