/* ==UserStyle==
@name Sarnai Codeberg
@namespace github.com/titembaatar/sarnai/userStyles/codeberg
@homepageURL https://github.com/titembaatar/sarnai
@version 0.1.0
@description Mongolian-inspired theme for Codeberg
@author Titembaatar
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["ovol:Ovol*", "khavar:Khavar"]
@var select darkFlavor "Dark Flavor" ["ovol:Ovol", "khavar:Khavar*"]
@var select accentColor "Accent" ["sarnai:Sarnai*", "anis:Anis", "chatsalgan:Chatsalgan", "els:Els", "uvs:Uvs", "nuur:Nuur", "mus:Mus", "yargui:Yargui"]
==/UserStyle== */
@-moz-document domain("codeberg.org") {
	[data-theme="codeberg-auto"] {
		@media (prefers-color-scheme: light) {
			#sarnai-theme(@lightFlavor);
		}
		@media (prefers-color-scheme: dark) {
			#sarnai-theme(@darkFlavor);
		}
	}

	#sarnai-theme(@flavor) {
		@sarnai: @sarnai-theme[@@flavor][@sarnai];
		@anis: @sarnai-theme[@@flavor][@anis];
		@chatsalgan: @sarnai-theme[@@flavor][@chatsalgan];
		@els: @sarnai-theme[@@flavor][@els];
		@uvs: @sarnai-theme[@@flavor][@uvs];
		@mus: @sarnai-theme[@@flavor][@mus];
		@nuur: @sarnai-theme[@@flavor][@nuur];
		@yargui: @sarnai-theme[@@flavor][@yargui];
		@text: @sarnai-theme[@@flavor][@text];
		@subtle: @sarnai-theme[@@flavor][@subtle];
		@muted: @sarnai-theme[@@flavor][@muted];
		@overlay: @sarnai-theme[@@flavor][@overlay];
		@surface: @sarnai-theme[@@flavor][@surface];
		@base: @sarnai-theme[@@flavor][@base];
		@accent: @sarnai-theme[@@flavor][@@accentColor];

		/* Base styles not needed, themed in upstream Gitea port. */
		/* START */
		--color-primary: @accent;
		--color-primary-contrast: @base;
		--color-primary-hover: lighten(@accent, 5%);
		--color-primary-dark-1: darken(@accent, 10%);
		--color-primary-dark-2: darken(@accent, 20%);
		--color-primary-dark-3: darken(@accent, 30%);
		--color-primary-dark-4: darken(@accent, 40%);
		--color-primary-dark-5: darken(@accent, 50%);
		--color-primary-dark-6: darken(@accent, 60%);
		--color-primary-dark-7: darken(@accent, 70%);
		--color-primary-light-1: lighten(@accent, 10%);
		--color-primary-light-2: lighten(@accent, 20%);
		--color-primary-light-3: lighten(@accent, 30%);
		--color-primary-light-4: lighten(@accent, 40%);
		--color-primary-light-5: lighten(@accent, 50%);
		--color-primary-light-6: lighten(@accent, 60%);
		--color-primary-light-7: lighten(@accent, 70%);
		--color-primary-alpha-10: fade(@accent, 90%);
		--color-primary-alpha-20: fade(@accent, 80%);
		--color-primary-alpha-30: fade(@accent, 70%);
		--color-primary-alpha-40: fade(@accent, 60%);
		--color-primary-alpha-50: fade(@accent, 50%);
		--color-primary-alpha-60: fade(@accent, 40%);
		--color-primary-alpha-70: fade(@accent, 30%);
		--color-primary-alpha-80: fade(@accent, 20%);
		--color-primary-alpha-90: fade(@accent, 10%);

		--color-secondary: @muted;
		--color-secondary-dark-1: lighten(@muted, 10%);
		--color-secondary-dark-2: lighten(@muted, 20%);
		--color-secondary-dark-3: lighten(@muted, 30%);
		--color-secondary-dark-4: lighten(@muted, 40%);
		--color-secondary-dark-5: lighten(@muted, 50%);
		--color-secondary-dark-6: lighten(@muted, 60%);
		--color-secondary-dark-7: lighten(@muted, 70%);
		--color-secondary-light-1: darken(@muted, 10%);
		--color-secondary-light-2: darken(@muted, 20%);
		--color-secondary-light-3: darken(@muted, 30%);
		--color-secondary-light-4: darken(@muted, 40%);
		--color-secondary-light-5: darken(@muted, 50%);
		--color-secondary-light-6: darken(@muted, 60%);
		--color-secondary-light-7: darken(@muted, 70%);
		--color-secondary-alpha-10: fade(@muted, 90%);
		--color-secondary-alpha-20: fade(@muted, 80%);
		--color-secondary-alpha-30: fade(@muted, 70%);
		--color-secondary-alpha-40: fade(@muted, 60%);
		--color-secondary-alpha-50: fade(@muted, 50%);
		--color-secondary-alpha-60: fade(@muted, 40%);
		--color-secondary-alpha-70: fade(@muted, 30%);
		--color-secondary-alpha-80: fade(@muted, 20%);
		--color-secondary-alpha-90: fade(@muted, 10%);

		/* colors */
		--color-red: @anis;
		--color-orange: @chatsalgan;
		--color-yellow: @els;
		--color-olive: @uvs;
		--color-green: @uvs;
		--color-teal: @mus;
		--color-blue: @nuur;
		--color-violet: @yargui;
		--color-purple: @yargui;
		--color-pink: @sarnai;
		--color-brown: @anis;
		--color-grey: @subtle;
		--color-black: @base;
		/* light variants - produced via Sass scale-color(color, $lightness: -10%) */
		--color-red-light: lighten(@anis, 10%);
		--color-orange-light: lighten(@chatsalgan, 10%);
		--color-yellow-light: lighten(@els, 10%);
		--color-olive-light: lighten(@uvs, 10%);
		--color-green-light: lighten(@uvs, 10%);
		--color-teal-light: lighten(@mus, 10%);
		--color-blue-light: lighten(@nuur, 10%);
		--color-violet-light: lighten(@yargui, 10%);
		--color-purple-light: lighten(@yargui, 10%);
		--color-pink-light: lighten(@sarnai, 10%);
		--color-brown-light: lighten(@anis, 10%);
		--color-grey-light: lighten(@subtle, 10%);
		--color-black-light: lighten(@base, 10%);
		/* dark 1 variants - produced via Sass scale-color(color, $lightness: -10%) */
		--color-red-dark-1: darken(@anis, 10%);
		--color-orange-dark-1: darken(@chatsalgan, 10%);
		--color-yellow-dark-1: darken(@els, 10%);
		--color-olive-dark-1: darken(@uvs, 10%);
		--color-green-dark-1: darken(@uvs, 10%);
		--color-teal-dark-1: darken(@mus, 10%);
		--color-blue-dark-1: darken(@nuur, 10%);
		--color-violet-dark-1: darken(@yargui, 10%);
		--color-purple-dark-1: darken(@yargui, 10%);
		--color-pink-dark-1: darken(@sarnai, 10%);
		--color-brown-dark-1: darken(@anis, 10%);
		--color-grey-dark-1: darken(@subtle, 10%);
		--color-black-dark-1: darken(@base, 10%);
		/* dark 2 variants - produced via Sass scale-color(color, $lightness: -20%) */
		--color-red-dark-2: darken(@anis, 20%);
		--color-orange-dark-2: darken(@chatsalgan, 20%);
		--color-yellow-dark-2: darken(@els, 20%);
		--color-olive-dark-2: darken(@uvs, 20%);
		--color-green-dark-2: darken(@uvs, 20%);
		--color-teal-dark-2: darken(@mus, 20%);
		--color-blue-dark-2: darken(@nuur, 20%);
		--color-violet-dark-2: darken(@yargui, 20%);
		--color-purple-dark-2: darken(@yargui, 20%);
		--color-pink-dark-2: darken(@sarnai, 20%);
		--color-brown-dark-2: darken(@anis, 20%);
		--color-grey-dark-2: darken(@subtle, 20%);
		--color-black-dark-2: darken(@base, 20%);
		/* other colors */
		--color-gold: lighten(@els, 20%);
		--color-white: @text;
		--color-diff-removed-word-bg: fade(@anis, 85%);
		--color-diff-added-word-bg: fade(@uvs, 85%);
		--color-diff-removed-row-bg:fade(@anis, 93%);
		--color-diff-moved-row-bg: fade(@nuur, 93%);
		--color-diff-added-row-bg: fade(@uvs, 93%);
		--color-diff-removed-row-border: fade(@anis, 93%);
		--color-diff-moved-row-border: fade(@nuur, 93%);
		--color-diff-added-row-border: fade(@uvs, 93%);
		--color-diff-inactive: @subtle;
		--color-error-border: lighten(@anis, 10%);
		--color-error-bg: @anis;
		--color-error-bg-active: @anis;
		--color-error-bg-hover: darken(@anis, 10%);
		--color-error-text: @base;
